	else if (theme=="Mocha") {

		$type = dark

        $rosewater =     245, 224, 220
        $flamingo =      242, 205, 205
        $pink =          245, 194, 231
        $mauve =         203, 166, 247
        $red =           243, 139, 168
        $maroon =        235, 160, 172
        $peach =         250, 179, 135
        $yellow =        249, 226, 175
        $green =         166, 227, 161
        $teal =          148, 226, 213
        $blue =          135, 176, 249
