        $sapphire =      116, 199, 236
        $sky =           137, 220, 235
        $lavender =      201, 203, 255
        $text =          217, 224, 238
        $subtext1 =      195, 186, 198
        $subtext0 =      152, 139, 162
        $overlay2 =      110, 108, 126
        $overlay1 =      87, 82, 104
