    if(theme != "Latte") {
        :root {
        color-scheme: dark;
        }
    }

    if (accent-color=="Rosewater") {
            $accent-color = $rosewater;
    } else if (accent-color=="Flamingo") {
            $accent-color = $flamingo
    } else if (accent-color=="Pink") {
        $accent-color = $pink
    } else if (accent-color=="Mauve") {
            $accent-color = $mauve
    } else if (accent-color=="Red") {
        $accent-color = $red
    } else if (accent-color=="Maroon") {
        $accent-color = $maroon
    } else if (accent-color=="Peach") {
        $accent-color = $peach
    } else if (accent-color=="Yellow") {
        $accent-color = $yellow
    } else if (accent-color=="Green") {
        $accent-color = $green
    } else if (accent-color=="Teal") {
        $accent-color = $teal
    } else if (accent-color=="Blue") {
        $accent-color = $blue
    } else if (accent-color=="Sapphire") {
        $accent-color = $sapphire
    } else if (accent-color=="Sky") {
        $accent-color = $sky
    } else if (accent-color=="Lavender") {
        $accent-color = $lavender
    }  else if (accent-color=="Gray") {
        $accent-color = $subtext0
    }

    /*-----------FOOTER---------*/
    :root {
        --accent: rgb($accent-color) !important;
        --accent-dim: rgba($accent-color, 0.5) !important;
        --crust: rgb($crust);
        --mantle: rgb($mantle);
        --base: rgb($base);
        --overlay: rgb($overlay0);
        --subtext0: rgb($subtext0);
        --subtext1: rgb($subtext1);
        --text: rgb($text);
    }
    /*------UNIVERSAL STYLES INCASE SOME GET MISSED------*/
    body {
        color: var(--text);
    }
    h1 {
        color: var(--accent);
    }
    .mimic-link {
        color: var(--accent);
    }
    /*-----------CHECKBOXES AND RADIOS----------*/
    .lfm-form-radio input[type=radio]:checked+label:before {
        border: 1px solid var(--accent);
        box-shadow: inset 0 0 0 4px var(--accent);
        background-color: var(--base);
    }
    .lfm-form-radio input[type=radio]+label:before {
        background-color: var(--crust);
        border: 2px solid rgb($surface1);
    }
    /*-----------REMOVE PRO SUBSCRIPTION REMINDERS------*/
    if removepromos {
        .your-progress .buffer-2, .secondary-nav-item--month, .secondary-nav-item--year, .auth-dropdown-pro-text {
            display: none !important;
        }
        .listening-report-promo--month, .listening-report-promo--year {
            display: none !important;
        }
    }

    /* HOME PAGE */

    .main-content,
    .container,
    .content-top-has-nav .content-top, .two-column-layout .content-top {
        background-color: var(--base);
    }

    .content-top-has-nav .content-top::after, .two-column-layout .content-top::after {
        border-top: 1px solid var(--accent-dim);
    }

    .homefeed .content-top .secondary-nav-item-link--active, .home-welcome-header {
        color: var(--text);
    }
    .secondary-nav-item-link {
        color: var(--subtext0);
    }

    .secondary-nav-item-link:focus,
    .secondary-nav-item-link:hover,
    .homefeed .content-top .secondary-nav-item-link--active,
    .homefeed .content-top .secondary-nav-item-link--active:focus,
    .homefeed .content-top .secondary-nav-item-link--active:hover{
        color: var(--text);
    }

    .homefeed .content-top .secondary-nav-item--artists .secondary-nav-item-link:hover::after,
    .homefeed .content-top .secondary-nav-item--albums .secondary-nav-item-link:hover::after,
    .homefeed .content-top .secondary-nav-item--tracks .secondary-nav-item-link:hover::after,
    .homefeed .content-top .secondary-nav-item--events .secondary-nav-item-link:hover::after,
    .homefeed--artists .content-top .secondary-nav-item-link--active::after,
    .homefeed--albums .content-top .secondary-nav-item-link--active::after,
    .homefeed--tracks .content-top .secondary-nav-item-link--active::after,
    .homefeed--events .content-top .secondary-nav-item-link--active::after
    {
        background-color: var(--accent-dim);
    }
    .recs-feed .recs-feed-cover-image-wrap:after {
        background-image: linear-gradient(180deg, transparent 0, rgba($mantle, 0.4) 50%, rgba($mantle, 0.7) 50%, rgba($mantle, 0.7) 100%);
    }

    .recs-feed .context {
        background: var(--mantle);
        box-shadow: inset 1px 0 #252525,inset -1px -1px var(--crust);
        color: var(--text);
    }
    .recs-feed .recs-feed-item {
        background: transparent;
        box-shadow: none;
    }
    a {
        color: var(--accent);
    }
    .stationlink-list .stationlink {
        color: var(--subtext0);
    }

    .stationlink-list .stationlink:focus, .stationlink-list .stationlink:hover {
        color:var(--text);
    }

    .stationlink-list .stationlink::after {
        border-bottom: 1px solid var(--accent-dim);
    }

    .stationlink-list .stationlink::before {
        background-color: var(--accent);
        -webkit-filter: invert(0%);
        filter: invert(0%);
    }



    .stationlink-list .stationlink:focus::before, .stationlink-list .stationlink:hover::before {
        background-color: var(--subtext0);
    }

    .subscribe-cta,
    .mpu-subscription-upsell,
    .mpu-subscription-upsell--mpu {
        display: none;
    }
    .join-cta {
        background-color: transparent;
    }
    .join-cta h3 {
        color: var(--accent);
    }


    .lazy-features-footer {
        display: none;
    }





    /* LIVE */





    /* MUSIC */

    .content-top-header {
        color: var(--accent);
    }

    .minimal-navigation-item-link, .minimal-navigation-item-link:focus, .minimal-navigation-item-link:hover {
        color: var(--subtext0);
    }

    .minimal-navigation-item-link--active, .minimal-navigation-item-link--active:focus, .minimal-navigation-item-link--active:hover {
        color: var(--text);
    }

    .music-section-heading {
        color: var(--text);
    }

    .music-more-artists-item-name a,
    .music-more-artists-item-name a:focus,
    .music-more-artists-item-name a:hover,
    .music-releases-item-name a,
    .music-releases-item-name a:focus,
    .music-releases-item-name a:hover {
        color: var(--text);
    }

    .music-releases-item-artist,
    .column-tracks-item-name,
    .column-tracks-item-artist,
    .music-gallery-artist{
        color: var(--text);
    }

    .music-section-rediscover-subscribe-banner-cta {
        background-color: var(--base);
    }

    /* PROFILE */
     /* BANNER */
     .header--overview .header-background {
        opacity: 0.4;
     }



    /* SEARCH */

    .artist-result-heading {
        color: var(--text);
    }

    .album-result-heading {
        color: var(--text);
    }



    /* RECCOMENDATIONS */
    .recs-feed-title a {
        color: var(--text) !important;
    }
    if (theme=="Latte") {
        .recs-feed-title a {
            color: var(--crust) !important;
        }
    }
    .recs-feed .context {
        background: var(--mantle);
        box-shadow: none;
        color: var(--subtext1);
    }
    .recs-feed-item--artist .context:after,
    .recs-feed .recs-feed-item--artist .recs-feed-avatar:after {
        background-color: rgba($yellow,1) !important;
    }
    .recs-feed-item--album .context:after,
    .recs-feed .recs-feed-item--album .recs-feed-avatar:after {
        background-color: rgba($green,1) !important;
    }
    .recs-feed-item--track .context:after,
    .recs-feed .recs-feed-item--track .recs-feed-avatar:after {
        background-color: rgba($blue,1) !important;
    }
    .secondary-nav {
        box-shadow: inset 0 -1px var(--accent);
    }
    .music-recommended-artists-artist-name,
    .music-recommended-artists-context,
    .pagination-page,
    .pagination-next
    {
        color: var(--text);
    }

    .music-recommended-albums-item-name,
    .music-recommended-albums-album-artist,
    .music-recommended-albums-album-context
    {
        color: var(--text);
    }

    .recommended-tracks-item-name,
    .recommended-tracks-item-artist
    {
        color: var(--text);
    }

    .big-tags-item-context {
        color: var(--text);
    }

    p {
        color: var(--subtext0);
    }




    /* CHARTS */

    .adaptive-skin-wrapper,
    .row{
        background-color: var(--base);
    }

    .two-column-layout .container {
        background-size: 0;
        background-position: 0 0;
        background-color: var(--base);
    }

    ._buffer-reset, .sidebar-heading {
        color: var(--accent);
    }

    .sidebar-group-heading {
        color: var(--text);
    }

    .resource-list--sidebar-item-name {
        color: var(--text);
    }
    .js-link-block.globalchart-item {
        background-color: var(--crust);
        box-shadow: 0 1px var(--base),inset 0 -1px var(--base);
    }
    .globalchart a, .globalchart a:focus, .globalchart a:hover {
        color: var(--text);
    }

    .footer-top-row {
        background-color: var(--mantle);
    }
    .two-column-layout .container {
        background-color: #00000000;
    }

    /* EVENTS */

    .events-filter,
    .events-filter-recommended,
    .dropdown-menu-clickable-button:focus,
    .dropdown-menu-clickable-button:hover
    {
        color: var(--text);
    }

    .events-filters
    {
        border-bottom: 1px solid var(--accent);
    }

    .events-list-item-venue--address,
    .events-list-item-event--lineup
    {
        color: var(--subtext0);
    }
    .events-list-anhv1 a,
    .events-list-anhv1 a:focus,
    .events-list-anhv1 a:hover,
    .events-list-item-event--title,
    .events-list-item-venue--title
    {
        color: var(--text);
    }




    /* FEATURES */

    .features-header .features-teaser-body,
    .features-teaser-wrap--promoted .features-teaser-body {
        background: var(--base);
    }

    .features-teaser-title a, .features-teaser-title a:focus, .features-teaser-title a:hover {
        color: var(--text);
    }




    /* FEATURES POST */

    .features-post-header-body {
        background-color: var(--base);
    }

    .features-post-header-title {
        color: var(--accent);
    }

    .features-post-artist {
        color: var(--text);
    }

    .features-post-byline-item.features-post-author {
        color: var(--text)
    }




    /* PROFILE */

    .header-avatar-add,
    .header-avatar-add:not(.cta--inactive):focus,
    .header-avatar-add:not(.cta--inactive):hover{
        background-color: var(--accent-dim);
    }
    .dropdown-menu-clickable,
    #share-library-artist,
    .dropdown-menu-clickable-item:active,
    body:not([data-whatinput=touch]) .dropdown-menu-clickable-item:focus,
    body:not([data-whatinput=touch]) .dropdown-menu-clickable-item:hover {
        background-color: var(--base);
    }
    .header--overview {
        background-color: var(--mantle);
    }
    .header-metadata .header-metadata-title, .header-scrobble-since, .header-title-display-name {
        color: rgb($subtext1);
    }
    .chartlist-play-button::before {
        background-color: var(--accent);
        -webkit-filter: invert(0%);
        filter: invert(0%);
    }

    .chartlist-play-button:focus:focus::before, .chartlist-play-button:focus:hover::before, .chartlist-play-button:hover:focus::before, .chartlist-play-button:hover:hover::before {
        box-shadow: 0 0 0 1px rgba(255,255,255,.15);
        background-color: var(--subtext0);
    }

    .text-colour-link, h2,
    .dropdown-menu-clickable-item--selected,
    .dropdown-menu-clickable-item--selected:focus,
    .dropdown-menu-clickable-item--selected:hover,
    .dropdown-menu-clickable-button,
    .dropdown-menu-clickable-button:hover,
    .dropdown-menu-clickable-button:active,
    .dropdown-menu-clickable-button.disclose-active {
        color: var(--accent);
    }
    .dropdown-menu-clickable-item,
    .dropdown-menu-clickable-item:active,
    body:not([data-whatinput=touch]) .dropdown-menu-clickable-item:focus,
    body:not([data-whatinput=touch]) .dropdown-menu-clickable-item:hover {
        color: var(--text);
    }
    .btn-secondary {
        background-color: var(--accent-dim);
        color: var(--crust);
    }

    body:not(.js-focus-visible) body:not([data-whatinput="touch"]) .btn-secondary:focus, body:not([data-whatinput="touch"]) .btn-secondary .focus-visible, body:not([data-whatinput="touch"]) .btn-secondary:hover {
        background-color: var(--accent);
        color: var(--crust);
    }
    input[type=password] {
        background-color: var(--crust) !important;
        border-color: var(--crust) !important;
    }


    @-webkit-keyframes AnimationName {
        0%{background-position:0% 50%}
        50%{background-position:100% 51%}
        100%{background-position:0% 50%}
    }
    @-moz-keyframes AnimationName {
        0%{background-position:0% 50%}
        50%{background-position:100% 51%}
        100%{background-position:0% 50%}
    }
    @keyframes AnimationName {
        0%{background-position:0% 50%}
        50%{background-position:100% 51%}
        100%{background-position:0% 50%}
    }

    .chartlist-row--highlight, .chartlist-row--now-scrobbling {
        /*background-color: #5a1717;*/
        background: linear-gradient(270deg, var(--base) 60%, rgba($accent-color, 0.2));
        background-size: 200% 200%;
        -webkit-animation: AnimationName 10s ease infinite;
        -moz-animation: AnimationName 10s ease infinite;
        animation: AnimationName 10s ease infinite;
    }

    .chartlist-name,
    .chartlist-artist {
        color: var(--subtext0);
    }

    .chartlist-break::after,
    .chartlist-row::after
    {
        border-bottom: 1px solid rgb($surface0);
    }

    .chartlist-row:hover {
        background-color: var(--crust);
    }
    .chartlist-count-bar-slug {
        background-color: transparent !important;
    }

    .chartlist-count-bar-slug {
        background-image: linear-gradient(to right , var(--accent), var(--accent));
        color: var(--crust);
    }
    .chartlist-count-bar-value {
        color: var(--crust);
    }
    a:focus .chartlist-count-bar-slug, a:hover .chartlist-count-bar-slug {
        background-color: var(--accent-dim);
    }

    .chartlist-count-bar-link,
    .chartlist-count-bar-link:focus, .chartlist-count-bar-link:hover{
        color: var(--text);
    }

    .listening-report-promo {
        background-color: var(--mantle);
    }
    .listening-report-promo:focus, .listening-report-promo:hover {
        background-color: var(--crust);
    }
    .listening-report-promo-title
    {
        color: var(--subtext0);
    }
    .listening-report-promo-date {
        color: var(--accent-dim);
    }

    .shout-form textarea {
        background-color: var(--crust);
    }

    .chartlist-index {
        color: var(--text);
    }

    .chartlist tr:nth-child(1) td.chartlist-index {
        font-weight: bold;
        color: var(--accent);
    }





    /* LISTENING REPORT */
    .user-dashboard-module {
        background-color: var(--base) !important;
    }
    .highcharts-background {
        fill: var(--base) !important;
    }
    .header-metadata-display, .scrobble-stats-info {
        color: var(--text);
    }
    .user-dashboard-legend-item:before {
        background: transparent !important;
    }
    .user-dashboard-legend-item--current::before {
        background-image: linear-gradient(159deg,var(--accent),var(--accent)) !important;
    }
    .user-dashboard-legend-item--comparison::before {
        background-image: linear-gradient(159deg,var(--accent-dim),var(--accent-dim)) !important;
    }
    .header-metadata-title, .user-dashboard-scrobble-stats-meta-description, .user-dashboard-scrobble-stats-previous {
        color: var(--subtext1);
    }
    .user-dashboard-heading {
        color: var(--accent);
    }

    .listening-report-top .secondary-nav {
      border-bottom: 1px solid var(--overlay);
    }
    .listening-report-brick {
      border: 1px solid var(--overlay);
      &:focus, &:hover {
        border: 1px solid var(--overlay);
        box-shadow: none;
      }
    }
    .listening-report-library-link {
      &, &:hover {
        color: var(--crust) !important;
        background-color: var(--accent) !important;
      }
    }
    .user-dashboard-history-subscribe-banner-cta {
        display: none;
    }

    .duration-data,
